String requestName = "searchCertificateCED"
String operationName = "Search Certificates"
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def holder = groovyUtils.getXmlHolder(requestName+"#response") 
int code

try{
  	code = holder.getNodeValue("//*:OperationCode") as Integer
	testRunner.testCase.testSuite.setPropertyValue( "operationCode", code.toString() )
	if(code > 0){ 
		throw new Exception("Invalid OperationCode response") 
	}
	long requestIdentifier = holder.getNodeValue("//*:RequestIdentifier") as Long
	testRunner.testCase.testSuite.setPropertyValue( "requestIdentifier", requestIdentifier.toString() )
	log.info operationName + ": OperationCode response: " + code.toString()
	log.info operationName + ": RequestIdentifier response: " + requestIdentifier.toString()
}catch(err){
	log.error operationName + ": OperationCode response: " + code.toString()
	throw Exception(operationName + ": Non-zero error code returned",err)
}

String requestName = "searchCertificatePoll"
String operationName = "Parse Certificates"
int pollWait = 2000
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def holder = groovyUtils.getXmlHolder(requestName+"#response") 
int code

try{
  	code = holder.getNodeValue("//*:OperationCode") as Integer
	testRunner.testCase.testSuite.setPropertyValue( "operationCode", code.toString() )
	log.info operationName + ": OperationCode response: " + code.toString()
	if(code == 10){
		//  unsuccessful response OperationCode == 10 - result not ready
		int loopIndex = testRunner.testCase.getPropertyValue( "loopIndex" ) as Integer
		if( loopIndex == null ){
			testRunner.testCase.setPropertyValue( "loopIndex",  '1' )
		}else{
			testRunner.testCase.setPropertyValue( "loopIndex", ++loopIndex.toString() )
		}
		if( loopIndex < 10 ){
			log.info operationName + ": Polling again in " + pollWait.toString() + " milliseconds"
			testRunner.gotoStepByName( requestName )
			sleep(pollWait)
		}else{
			testRunner.testCase.setPropertyValue( "loopIndex", '1' )
			throw new Exception("Failed to get successful response") 
		}
	}else if(code > 0){ 
		//  unsuccessful reponse
		throw new Exception("Invalid OperationCode response") 
	}else{
		//  successful response OperationCode == 0
		log.info operationName + ": Certificates found"
	}
}catch(err){
	log.error " " + operationName + ": OperationCode response: " + code.toString()
	log.error " " + operationName + " Exception - " + err.getMessage()
}
